#!/usr/bin/env python# LIBPATH.py V1import osmypaths=os.environ['PATH'].split(':')valid_path=[]invalid_path=[]duplicated_path=[]for dir in mypaths:    path_hand = os.popen('readlink -f \"'+dir+'"')    full_dir = path_hand.read().strip()    path_hand.close()    if full_dir=='':        invalid_path.append(dir)    elif full_dir in valid_path:        duplicated_path.append(full_dir)    else:        valid_path.append(full_dir)